void main_delay_us(unsigned long us)
{
	uint16_t t0 = MAIN_TIM->CNT;
	for (;;) {
		int t = MAIN_TIM->CNT;
		if (t < t0){
			t += 0x10000;
		}

		if (us < t - t0)
			return;

		us -= t - t0;
		t0 = t;
	}
}


/*******************************************************************************
* Function Name  : main_delay_ms
* Input          : 
* Output         : None
* Return         : 
* Description    : 
*******************************************************************************/

void main_delay_ms(unsigned long ms)
{
	main_delay_us(ms * 1000);
}


